document.addEventListener("astro:page-load",()=>{const s=document.querySelectorAll("section"),n=document.querySelectorAll("header nav a"),r=e=>{e.forEach(c=>{c.isIntersecting&&n.forEach(o=>{o.getAttribute("aria-label")==c.target.id?o.classList.add("text-blue-500"):o.classList.remove("text-blue-500")})})},t=new IntersectionObserver(r,{root:null,rootMargin:"0px",threshold:.3});s.forEach(e=>{t.observe(e)}),document.onvisibilitychange=()=>{document.visibilityState==="hidden"?t.disconnect():s.forEach(e=>{t.observe(e)})}});
